import React from 'react'

class GetRandomUsers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            loading: false,
        };
    }

    componentDidMount() {
        fetch('https://randomuser.me/api/')
        .then(resp => resp.json(),)
        .then( resp => {
            this.setState({
                data: resp.results,
                loading: true,
            })
        })
    }

    render() {
        return (
            <p>{this.state.response}</p>
        )
    }
}

export default GetRandomUsers;
