import React from 'react'

class GetRandomUsers extends React.Component{
    constructor(props){
        this.state = { response: "" };
    }

    componentDidMount(){
        fetch('https://randomuser.me/api/').then(
            (resp) => {
                this.setState(
                    {response: resp.json()}
                )                
            }
        )
    }

    render(){
        return(
            <p>{this.state.response}</p>
        )
    }
}

export default GetRandomUsers;
