import React from 'react'

class GetRandomUsers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            loading: false,
        };
    }

    componentDidMount() {
        fetch('https://randomuser.me/api/')
        .then(resp => resp.json(),)
        .then( resp => {
            this.setState({
                data: resp.results,
                loading: true,
            })
        })
    }

    render() {

        let {data, loading} = this.state

        if(!loading){
            return(
                <div>Loading...</div>
            )
        }

        else{
            return(
                <div>
                    {data.map(itm => 
                        <img src={itm.picture.medium} />
                    )}
                    <table class="table table-bordered">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
                </div>
            )
        }
    }
}

export default GetRandomUsers;
