import React from 'react'

class GetRandomUsers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            loading: false,
        };
    }

    componentDidMount() {
        fetch('https://randomuser.me/api/')
        .then(resp => resp.json(),)
        .then( resp => {
            this.setState({
                data: resp.results,
                loading: true,
            })
        })
    }

    render() {

        let {data, loading} = this.state

        if(!loading){
            return(
                <div>Loading...</div>
            )
        }

        else{
            return(
                <div>
                    {data.map(itm => 
                        <img src={itm.picture.medium} />
                    )}
                </div>
            )
        }
    }
}

export default GetRandomUsers;
