import React from 'react'

class GetRandomUsers extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            loading: false,
        };
    }

    componentDidMount() {
        fetch('https://randomuser.me/api/?results=5')
            .then(resp => resp.json(),)
            .then(resp => {
                this.setState({
                    data: resp.results,
                    loading: true,
                })
            })
    }

    render() {

        let { data, loading } = this.state

        if (!loading) {
            return (
                <div>Loading...</div>
            )
        }

        else {
            return (
                <div class="container">
                    <table class="table table-bordered center">
                        <thead>
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Picture</th>
                                <th scope="col">Name</th>
                                <th scope="col">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <th scope="row">1</th>
                                {data.map(itm => <td><img src={itm.picture.medium} /></td>)}
                                {/* <td>{data.map(itm => {itm.name.title, itm.name.first, itm.name.last} )}</td> */}
                                <td>{data[0].name.title + " " + data[0].name.first + " " + data[0].name.last}</td>
                                <td><button class="buttun btn-danger">x</button></td>
                            </tr>
                            <tr>
                                {
                                    data.map(function take(itm) {
                                        {
                                            <td><img src={itm.picture.medium} /></td>
                                        }{
                                            <><td>{data[0].name.title + " " + data[0].name.first + " " + data[0].name.last}</td><td><button class="buttun btn-danger">x</button></td></>
                                        }

                                    })
                                }
                                <th scope="row">2</th>
                                <td>Jacob</td>
                                <td>Thornton</td>
                                <td>@fat</td>
                            </tr>
                            <tr>
                                <th scope="row">3</th>
                                <td colspan="2">Larry the Bird</td>
                                <td>@twitter</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            )
        }
    }
}

export default GetRandomUsers;
